INSERT INTO `lrb_note`(`id`, `note_id`, `note_url`, `title`, `video_poster`, `video_src`, `images_lists`, `content`, `like_num`, `comment_num`, `star_num`, `publish_date`, `author_name`, `author_image`, `author_id`, `description`, `board_id`, `tag_str`, `note_str`, `comment_str`, `spider_time`) VALUES (8, '5e894d2800000000010079a5', 'https://www.xiaohongshu.com/discovery/item/5e894d2800000000010079a5', '两年过公考、法考、注会5门、税务师 | 搭配法', 'https://ci.xiaohongshu.com/2a07b1d9-6da8-57bf-b7dd-db46c8bae8a0', 'http://v.xiaohongshu.com/312d142ede147e8284e293aa04300947cd0ed17e_v1_ln?sign=8eeba188fe583337fd17d452160d45aa&t=601ac880', '', '大家好！我是陪伴大家备考的伪学霸！\n.\n看完视频后，大家可能会有疑惑，怎么没说第一年考啥、第二年考啥呢？\n其实是想让大家自己搭配注会六科如何备考，因为注会六科中各科无特别大联系，只有难易之分，所以，只要难易搭配，就是科学的！具体难易之分，可以参考我上一篇视频笔记！所以只要选好注会考啥，税务师直接顺带着就考了，备考时，注会考试前只学注会，税务师科目在考完注会后的20多天过一下就好，具体如下：\n🌈考注会会计➕财管，顺带考税务师财务与会计\n🌈考注会税法，顺带考税务师税法一、税法二、涉税实务\n🌈考注会经济法，顺带考税务师涉税法律\n.\n此外，大家注意，税务师考试难度与注会根本没法比，特别简单！税务师五门中四门全选择，只有涉税实务有主观题，可想而知比注会简单多少！只要你认真学注会科目，税务师直接就过了！\n.\n如果大家不知道注会六门如何搭配，可以参考我上一篇视频笔记！\n@视频薯 @校园薯 @小红叔 @薯队长\n', 1621, 109, 1825, '2020-04-05 11:14', '一个幸运的伪学霸', 'https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@240w_240h_90q_1e_1c_1x.jpg', '5e3bd70700000000010026b7', '大家好！我是陪伴大家备考的伪学霸！ . 看完视频后，大家可能会有疑惑，怎么没说第一年考啥、第二年考啥呢？ 其实是想让大家自己搭配注会六科如何备考，因为注会六科中各科无特别大联系，只有难易之分，所以，只', '5ea80a120000000001000e27', '[]', '[{\"note_id\": \"5fc5b20800000000010088a9\", \"note_desc\": \"两年，我考过了公考、法考、注会五门和税务师\"}, {\"note_id\": \"5fd478f9000000000101f40d\", \"note_desc\": \"你起不来，每早5点等你，走的慢但不后退！\"}, {\"note_id\": \"5f9bdad8000000000100080c\", \"note_desc\": \"day47 只能睡四小时了 在职一天学习9H\"}, {\"note_id\": \"5ff077910000000001002783\", \"note_desc\": \"CPA6科的老师推荐！零基础适用\"}, {\"note_id\": \"5f56e35e000000000101ca62\", \"note_desc\": \"30逆袭：在职且照顾俩宝，学习时间的安排！\"}, {\"note_id\": \"5fd22e790000000001006ba3\", \"note_desc\": \"所谓幸运，可能只是努力埋下的伏笔\"}, {\"note_id\": \"5f091a7f00000000010055eb\", \"note_desc\": \"day24/5点起床的自律生活/你真的努力了吗？\"}, {\"note_id\": \"5f81bde5000000000101e7e1