INSERT INTO `lrb_note`(`id`, `note_id`, `note_url`, `title`, `video_poster`, `video_src`, `images_lists`, `content`, `like_num`, `comment_num`, `star_num`, `publish_date`, `author_name`, `author_image`, `author_id`, `description`, `board_id`, `tag_str`, `note_str`, `comment_str`, `spider_time`) VALUES (30, '5ebcb871000000000100079d', 'https://www.xiaohongshu.com/discovery/item/5ebcb871000000000100079d', '我们两年过公考、法考、注会五门和税务师！', '', '', '[\"background-image: url(\\\"https://ci.xiaohongshu.com/7ea9b3b4-b68e-5116-b5c6-e765d43d65d6/format/webp\\\");\"]', '女朋友5.15生日啦，就想表白她❗\n·\n🌈哈喽，大家好！我是陪伴大家备考的伪学霸！在两年内通关公考、法考、注会五门和税务师考试！\n🌈分享过自己的成绩与经历后，总会有人问我，你有这么长时间学习，肯定没结婚，没女朋友吧？女朋友不用陪吗？不用干家务吗？\n🌈我确实没结婚，但是我有一个爱我、支持我、理解我的女朋友！在我学习期间，她为我搭配好健康的饮食，一直陪伴在我的身边，可以说我可以这么努力，都是她在给我动力！只有一起经历过，才能明白陪伴的可贵！\n🌈所以说，成绩不是我的，是我们的！我愿意为她去奋斗，为我们创造更美好的未来！\n·\n💎每当我努力不下去的时候，会想想父母，也会想想她！我会想我的女朋友那么漂亮，肯定想穿最美丽的衣服，肯定想背最贵的包，肯定要用最好的化妆品，肯定想住最豪的洋房，肯定要有最浪漫的旅行！\n💎我都要给她，人都是活一次，谁都有精彩的权利！别人的女朋友有，我必须要为我的女朋友创造，因为我爱她，我要把最好的给她！所以为了她，我必须奋斗！\n·\n❤️亲爱的，我爱你！感谢你为我的付出！未来的路，我们一起走！执子之手，与子偕老！\n💓每个人都有奋斗的理由，理由虽不同，但是方向一定相同！\n💖如果现在还找不到奋斗的方向，那可能现在还不能理解这个词语“责任”！我的价值观告诉我，一个男人的责任是要让爱人衣食无忧，要让父母安享晚年！\n·\n💘亲爱的，生日快乐！我爱你！\n·\n🎉看到的小伙伴们留下红心，给个祝福噢~祝愿大家都能有情人终成眷属，都能学有所成，心想事成，都能有一个远大前程！\n@校园薯 @生活薯\n', 336, 99, 76, '2020-05-14 11:18', '一个幸运的伪学霸', 'https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@240w_240h_90q_1e_1c_1x.jpg', '5e3bd70700000000010026b7', '女朋友5.15生日啦，就想表白她❗ · 🌈哈喽，大家好！我是陪伴大家备考的伪学霸！在两年内通关公考、法考、注会五门和税务师考试！ 🌈分享过自己的成绩与经历后，总会有人问我，你有这么长时间学习，肯定', '5ea80d860000000001001855', '[]', '[{\"note_id\": \"5ea01b730000000001009f48\", \"note_desc\": \"两年过公考、法考、注会五门和税务师|经验\"}, {\"note_id\": \"5e7328f80000000001003510\", \"note_desc\": \"两年通关公考、法考、税务师、注会5门|教你\"}, {\"note_id\": \"5e782774000000000100bf99\", \"note_desc\": \"两年通关公考、法考、注会5门、税务师|经验\"}, {\"note_id\": \"5e8befbf000000000100b933\", \"note_desc\": \"两年过公考、法考、注会5门和税务师|骂醒你\"}, {\"note_id\": \"5e9bc304000000000100aa81\", \"note_desc\": \"两年过公考、法考、注会五门和税务师考试！\"}, {\"note_id\": \"5e7ac9c0000000000100b7a4\", \"note_desc\": \"两年通关公考、法考、注会5门、税务师|坚持\"}, {\"note_id\": \"5e7191810000000001008f54\", \"note_desc\": \"2年通关公考、法考、注会5门、税务师|规划\"}, {\"note_id\": \"5e8404810000000001003abc\", \"note_desc\": \"两年通关公考、法考、注会5门、税务师|经验\"}]', '[{\"comment_text\": \"你女朋友真好，你也懂得感恩。我陪我男朋友度过了他最艰难的时刻，为他做好一切后勤，我为了他去了北京。然鹅去年我考研的时候经常和我发火摔东西，从不做家务，不做饭，考前一个星期半夜家暴我，果断分手。现在踏上二战考研+法考之路\", \"nickname\": \"Abby\", \"userid\": \"5c83adab0000000011008ea1\", \"publish_time\": \"2020-05-14\", \"avatar\": \"https://img.xiaohongshu.com/avatar/be4e54a984a21be925a215a08fb36c8b.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"32\"}, {\"comment_text\": \"你好厉害，你女朋友更厉害\", \"nickname\": \"嗯嗯\", \"userid\": \"565998c182ec39635bad251e\", \"publish_time\": \"2020-05-14\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5d99c095de356b0001527184.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"9\"}, {\"comment_text\": \"赞赞的，棒棒哒。\", \"nickname\": \"小马🐴\", \"userid\": \"5c919958000000001000a00e\", \"publish_time\": \"2020-05-20\", \"avatar\": \"https://img.xiaohongshu.com/avatar/be4e54a984a21be925a215a08fb36c8b.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"3\"}, {\"comment_text\": \"♥️真棒，祝你女朋友生日快乐呀～\", \"nickname\": \"羁绊＿\", \"userid\": \"5a7516b94eacab735e4ccaad\", \"publish_time\": \"2020-05-14\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5a751afcd1d3b97ba5492d26.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"3\"}, {\"comment_text\": \"妈耶，原来有女朋友呀，还在想怎么有这么多的时间复习\", \"nickname\": \"花卷儿\", \"userid\": \"56ffe09d4775a72987779025\", \"publish_time\": \"2020-05-14\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5fae5beefc2ec20001974e63.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"3\"}]', '2021-02-02 13:46:31');
