INSERT INTO `lrb_note`(`id`, `note_id`, `note_url`, `title`, `video_poster`, `video_src`, `images_lists`, `content`, `like_num`, `comment_num`, `star_num`, `publish_date`, `author_name`, `author_image`, `author_id`, `description`, `board_id`, `tag_str`, `note_str`, `comment_str`, `spider_time`) VALUES (41, '5e95279f0000000001000220', 'https://www.xiaohongshu.com/discovery/item/5e95279f0000000001000220', '做好的笔记如何实现二次利用？纯干货！', '', '', '[\"background-image: url(\\\"https://ci.xiaohongshu.com/34048473-2340-5fea-b64e-f2d649576e7d/format/webp\\\");\"]', '成功没有捷径，勤奋绝对是诀窍！不做笔记是偷懒，学习绝对事倍功半！做好笔记还不够，充分利用很关键❗\n.\n我在去年备考司法考试，注会会计、审计、财管，税务师涉税实务、涉税法律、财务与会计，总共记了20多本笔记！\n.\n很多人问我如何充分利用自己辛辛苦苦记得笔记，整理了一下自己的心得，分享给大家！\n✨✨✨✨✨✨✨✨\n🌿①为笔记写标题，做目录。当我们完成一科的学习，记完一科的笔记之后，要给自己的笔记做标题，编目录。如此过程，不但是对本科知识点的再一次回顾，更有利于形成本科完整的知识体系。\n🌸②看笔记时，只看标题，先默背标题下的内容，或者把笔记关键部分先遮住，靠前面的内容提示把关键内容背出来。\n🍀③翻阅笔记时要学会做减法，笔记不是看一次的，是让我们反复翻阅，每一次翻阅我们都要重新规划新的重点，并且把自己已经完全掌握的用符号删去，这样可以提高学习效率。\n🌻④应该及时修改整理笔记，笔记应该是动态的，不断发展的，对于笔记中记录错误的内容，要及时修正，避免此类内容在脑海中形成印象，让我们带着错误的知识进考场。而修正笔记的过程，也是我们进步的过程。\n🌷⑤纸上得来终觉浅，想要掌握还得背。不要为了记笔记而记笔记，我们记笔记的最终目的是要掌握知识点，所以不能用记笔记代替背诵知识点。\n✨✨✨✨✨✨✨✨\n最后说一句，笔记必须记，哪怕你以后不用，哪怕是抄书，如果实在不会做笔记，实在嫌弃麻烦，那就把书上的内容抄一下也比不做笔记好，因为笔记最重要的作用不是二次利用，而是记笔记时的手脑并用，这样能让我们对知识点的记忆更加深刻。记住，动手记笔记怎么也要比光看学习效率高，更容易让我们记住知识点。\n.\n想要成绩，分享给大家两个词:踏实，勤奋！\n@校园薯 @薯队长 @小红叔\n', 145, 42, 159, '2020-04-14 11:01', '一个幸运的伪学霸', 'https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@240w_240h_90q_1e_1c_1x.jpg', '5e3bd70700000000010026b7', '成功没有捷径，勤奋绝对是诀窍！不做笔记是偷懒，学习绝对事倍功半！做好笔记还不够，充分利用很关键❗ . 我在去年备考司法考试，注会会计、审计、财管，税务师涉税实务、涉税法律、财务与会计，总共记了20多本', '5ea80bdd0000000001007ab2', '[]', '[{\"note_id\": \"5e6e1f3f00000000010035b6\", \"note_desc\": \"如何高效记笔记？|2年过法考、注会、公务员\"}, {\"note_id\": \"5e7882da0000000001001333\", \"note_desc\": \"📚中学做笔记方法-学习方法本子推荐\"}, {\"note_id\": \"5e6a6196000000000100b01e\", \"note_desc\": \"如何高效做笔记㊙️学霸都在用的记笔记法✔\"}, {\"note_id\": \"5e7b34d300000000010092ed\", \"note_desc\": \"人大学姐谈：我的高效记笔记法\"}, {\"note_id\": \"5e4274460000000001009ec4\", \"note_desc\": \"两年过法考、税务师、cpa5门经验|法考篇四\"}, {\"note_id\": \"5e7057660000000001003063\", \"note_desc\": \"复旦考研初试第一，我的笔记和别人不一样\"}, {\"note_id\": \"5e64ce250000000001004595\", \"note_desc\": \"关于英语笔记|利用好A4纸❗️坚持积累别放弃\"}, {\"note_id\": \"5e78d81f000000000100072d\", \"note_desc\": \"自然地理⛰这些图就够啦‼️学渣逆袭建议收藏\"}, {\"note_id\": \"5e3fb665000000000100aea9\", \"note_desc\": \"初中数学逆袭宝典！教你怎么考高分（第二弹）\"}]', '[{\"comment_text\": \"发现自己不会记笔记，什么东西都想抄上去\", \"nickname\": \"爱笑的小眼睛\", \"userid\": \"5e1aca3a00000000010059f7\", \"publish_time\": \"2020-04-15\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5e4d309d233e4f00017b1335.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"8\"}, {\"comment_text\": \"我也特别喜欢写下来，但是很多时候，很慢，无法短时间通过难度高的考试\", \"nickname\": \"430\", \"userid\": \"5cdceaae000000001003be4c\", \"publish_time\": \"2020-05-21\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5fd2d1ca014db10001ed9c1c.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"0\"}, {\"comment_text\": \"今年打算考三科注会，觉得再抄笔记又不够时间了学习了，还是效率低啊\", \"nickname\": \"licy200710\", \"userid\": \"55e93e46f5a26356d3da8f38\", \"publish_time\": \"2020-05-20\", \"avatar\": \"https://img.xiaohongshu.com/avatar/ce8dcb873a0993b85c623836a5030a06.png@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"0\"}, {\"comment_text\": \"能分享一期你的笔记实拍吗？想学习怎么记笔记，每次记着记着就跟抄书一样了\", \"nickname\": \"女汉子💓潔宝宝\", \"userid\": \"5a2f28ccb1da14653b3fa6a8\", \"publish_time\": \"2020-05-19\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5fb3777268a0c1000157a325.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"0\"}, {\"comment_text\": \"讲义可以直接当笔记么\", \"nickname\": \"欣潼\", \"userid\": \"5af7e65c11be10604fe91de6\", \"publish_time\": \"2020-05-08\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5af7e6b3d2c8a51c5e8b4f49.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"0\"}]', '2021-02-02 13:47:22');
INSERT INTO `lrb_note`(`id`, `note_id`, `note_url`, `title`, `video_poster`, `video_src`, `images_lists`, `content`, `like_num`, `comment_num`, `star_num`, `publish_date`, `author_name`, `author_image`, `author_id`, `description`, `board_id`, `tag_str`, `note_str`, `comment_str`, `spider_time`) VALUES (42, '5ff91c5c000000000100b603', 'https://www.xiaohongshu.com/discovery/item/5ff91c5c000000000100b603', '如何学习才能过目不忘？ | 自创六遍学习法', '', '', '[\"background-image: url(\\\"https://ci.xiaohongshu.com/e8a580e8-da23-5af7-e16a-bf3e6dc8b840/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/a11e5f27-db7c-ca6b-ae40-0815a1842bf1/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/e068bbc1-3d1d-65c9-6411-6ba7f52711a0/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/d3005226-015d-fca3-2f1f-c9878553211e/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/5e67b8c2-e723-9f62-8cff-c04dd2211fae/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/68be2341-c032-f7ff-e454-9bf9ff7a60c9/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/826869f4-24bc-69be-6752-5d9c4e705464/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/fd1e1096-adb1-4d9b-1c43-c01a4a5d9ba1/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/a1ef4e3e-e230-2448-8001-7f68d8b04cf6/format/webp\\\");\"]', '我学习的时候都在说“你这么学都快学傻了”“学这么多遍也不可能都记住，这么多科怎么可能都考过”，我就笑笑不说话！现在我能看出他们的羡慕和不可思议！嗯，这种感觉很好！\n⬇️\n第一遍学习:听网课➕记笔记\n第一遍学习最重要，几乎能决定这科考试是否能通过，所以一定要重视，绝不能有“这遍先随便看看，反正还有好几轮复习呢”的思想！\n第一遍学习建议时长:1个月-2个月（具体按科目而定），讲义在听课的时候要标记自己认为的重点，每学完一章配合完成本章习题，每天最后的学习时间要回顾今天所记的笔记！\n·\n第二遍学习:刷题\n当一科完全学完后，前面学的知识可能快要忘了，这时候要大量刷题，一是通过做题引出知识点再次记忆，二是熟悉出题模式，做到熟练运用知识点，三是提高做题速度！\n第二遍学习建议时长:一个月，刷题的同时要准备错题本，每日看错题\n·\n第三遍学习:背诵笔记\n笔记不光要记，还要拿来用！能记入笔记本的，一定是你当时的知识盲区，所以要多重复记忆！\n第三遍学习建议时长:5-10天，背诵笔记的时候要先看笔记标题，自己联想知识点，然后再看知识点，最后背诵知识点！\n·\n第四遍学习:回顾讲义\n拿出听课时用的讲义，把上面自己标记的重点再最后看一遍，讲义上肯定有很多例题，能上讲义的例题，不是基础题型就是重点题型，一定要熟练掌握\n第四遍学习建议时长:10-15天\n·\n第五遍学习:模拟考试\n考前一定要按照真实的考试时间去模拟考试，一是让自己在真正考试的时间点脑海中正好是这科的思维，二是模拟考试环境，到了考场才不会慌，更好的支配时间，不会出现做不要完题的情况！\n第五遍学习建议时长:10-15天，主要是做历年真题（近10年内，视具体考试科目而定）和模拟套题。先做，然后对答案，再理解记忆错题，最后记入错题本！\n·\n第六遍学习:刷错题本\n马上就要考试了，最后的复习时间依然是刷题，不过这次是刷错题！\n第六遍学习建议时长:2-3天\n@校园薯 @薯队长\n', 1662, 52, 1533, '2021-01-09 11:00', '一个幸运的伪学霸', 'https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@240w_240h_90q_1e_1c_1x.jpg', '5e3bd70700000000010026b7', '我学习的时候都在说“你这么学都快学傻了”“学这么多遍也不可能都记住，这么多科怎么可能都考过”，我就笑笑不说话！现在我能看出他们的羡慕和不可思议！嗯，这种感觉很好！ ⬇️ 第一遍学习:听网课➕记笔记 第', '5ea80bdd0000000001007ab2', '[]', '[{\"note_id\": \"5f5ae6ed000000000100519c\", \"note_desc\": \"如何刷题才能效率翻倍|不收藏你就亏啦！\"}, {\"note_id\": \"5f9564990000000001009883\", \"note_desc\": \"从160到240+，这样学文综你也可以逆袭！\"}, {\"note_id\": \"5f9237e70000000001000184\", \"note_desc\": \"给我仅增的5个粉丝说说我公务员备考之路😂\"}, {\"note_id\": \"5f9f6c0a000000000100ae31\", \"note_desc\": \"两个月如何备注N2（无偿分享资料）\"}, {\"note_id\": \"5f9fa170000000000100998f\", \"note_desc\": \"刚刚报名自考不知道怎样学习看过来！！！\"}, {\"note_id\": \"5f73ba7b00000000010014bd\", \"note_desc\": \"教师招聘笔试攻略\"}, {\"note_id\": \"5f9399420000000001008bea\", \"note_desc\": \"一年在职备考通关CPA六科经验贴-会计篇\"}, {\"note_id\": \"5f9fd555000000000101c689\", \"note_desc\": \"基础弱的初三生，如何2个月提高100分？\"}, {\"note_id\": \"5f8d65660000000001004e6f\", \"note_desc\": \"N19.㊙️学霸初中三年学习规划|学渣逆袭\"}, {\"note_id\": \"5f813bf9000000000100ac60\", \"note_desc\": \"中考必看｜初中生物地理政治学习方法\"}]', '[{\"comment_text\": \"我比较关心你的投入产出比，你现在挣多少钱\", \"nickname\": \"🎀是付大妞吖🎀\", \"userid\": \"566b639382718c1358aa08c4\", \"publish_time\": \"01-09\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5b254309d2c8a50220fcb8b3.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"14\"}, {\"comment_text\": \"你是纯分享还是卖东西啊\", \"nickname\": \"茉莉\", \"userid\": \"5c613619000000001b00ca19\", \"publish_time\": \"01-11\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5d982c8251168f0001eb2bf6.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"3\"}, {\"comment_t