INSERT INTO `lrb_note`(`id`, `note_id`, `note_url`, `title`, `video_poster`, `video_src`, `images_lists`, `content`, `like_num`, `comment_num`, `star_num`, `publish_date`, `author_name`, `author_image`, `author_id`, `description`, `board_id`, `tag_str`, `note_str`, `comment_str`, `spider_time`) VALUES (62, '5fd43cac0000000001008498', 'https://www.xiaohongshu.com/discovery/item/5fd43cac0000000001008498', '永远不要说学习没用，可能是你没用丨化学篇', 'https://ci.xiaohongshu.com/061cbf21-1f0a-e329-4d05-0364f027a550', 'http://v.xiaohongshu.com/5644f0361fcb94b4c8b57e35fd23cd51accb2fec_v1_ln?sign=2de8bb8bc2c340afff3507f5af9d1d8b&t=601ac880', '', '最近有太多同学问我，高二了自己成绩还是很差，该如何提高，所以我继英语、数学提分经验分享之后，又开始呕心总结了化学经验。为了鼓励大家继续坚持，评论里选4位同学送《名师敲重点》教辅资料，必须做完哦！\n以前玩就玩了，从现在开始也不晚！想要努力学习，无奈短板太多无从下手，是不是很焦虑?我理解大家的心情，因为当时的我，和大家一样，什么都不会！我当时数理化根本听不懂，请了补课老师补到0点，自己回家会学到2点，无数次想放弃，因为不甘心，无数次又坚持下来。虽然高三升学考试只考了200多分，但是通过一年的努力，我从200+提高到560分上211。\n在这期间我也形成了自己学化学的方法，现在把经验分享给大家！\n一、思维导图\n二、必刷题+小灶课\n《手写笔记6.0版》\n《名师敲重点》把知识点、重难点、习题、模拟真题全涵盖了。对于高三时间紧张、精力有限的我们，选择这一本书就够了！\n有独特的虹膜记忆法，用这个方法可以记得更牢固。\n三、如何最大吸收老师传达的知识\n四、联想记忆+找规律\n五、笔记总结\n', 231, 29, 129, '2020-12-12 11:44', '一个幸运的伪学霸', 'https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@240w_240h_90q_1e_1c_1x.jpg', '5e3bd70700000000010026b7', '最近有太多同学问我，高二了自己成绩还是很差，该如何提高，所以我继英语、数学提分经验分享之后，又开始呕心总结了化学经验。为了鼓励大家继续坚持，评论里选4位同学送《名师敲重点》教辅资料，必须做完哦！ 以前', '5ea80d2f0000000001007dd2', '[]', '[{\"note_id\": \"5fdf15cd0000000001007f55\", \"note_desc\": \"“我走的很慢，但我从不后退”\"}, {\"note_id\": \"5fd478f9000000000101f40d\", \"note_desc\": \"你起不来，每早5点等你，走的慢但不后退！\"}, {\"note_id\": \"5f9bdad8000000000100080c\", \"note_desc\": \"day47 只能睡四小时了 在职一天学习9H\"}, {\"note_id\": \"5ff077910000000001002783\", \"note_desc\": \"CPA6科的老师推荐！零基础适用\"}, {\"note_id\": \"5f56e35e000000000101ca62\", \"note_desc\": \"30逆袭：在职且照顾俩宝，学习时间的安排！\"}, {\"note_id\": \"5fd22e790000000001006ba3\", \"note_desc\": \"所谓幸运，可能只是努力埋下的伏笔\"}, {\"note_id\": \"5f091a7f00000000010055eb\", \"note_desc\": \"day24/5点起床的自律生活/你真的努力了吗？\"}, {\"note_id\": \"5f81bde5000000000101e7e1\", \"note_desc\": \"30岁想要翻盘逆袭的目标是考公务员和研究生\"}, {\"note_id\": \"5fc7a2aa0000000001004a32\", \"note_desc\": \"在职党每天怎么沉浸学习4h？\"}, {\"note_id\": \"5fc62ba9000000000101f109\", \"note_desc\": \"会计师事务所的加班有多可怕？\"}]', '[{\"comment_text\": \"为鼓励高中小粉丝们继续加油，12.15中午12点，选4位同学送《名师敲重点》教辅资料，数理化生。好的结果值得让人竭尽全力，加油少年们！\", \"nickname\": \"一个幸运的伪学霸(作者)\", \"userid\": \"5e3bd70700000000010026b7\", \"publish_time\": \"2020-12-12\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"10\"}, {\"comment_text\": \"开🎁啦～恭喜@成真@正在努力的小糖@唯爱s@winner🥳\", \"nickname\": \"一个幸运的伪学霸(作者)\", \"userid\": \"5e3bd70700000000010026b7\", \"publish_time\": \"2020-12-15\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"1\"}, {\"comment_text\": \"学长，我要我要我要！\", \"nickname\": \"devilmad\", \"userid\": \"5ee26a7f000000000101f3c1\", \"publish_time\": \"2020-12-12\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5fbe99e40baeb70001f46c8d.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"3\"}, {\"comment_text\": \"你哪里的钱啊\", \"nickname\": \"陌年微凉\", \"userid\": \"5e31391a000000000100203c\", \"publish_time\": \"01-24\", \"avatar\": \"https://img.xiaohongshu.com/avatar/6016c3b0b689131e34e82063.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"0\"}, {\"comment_text\": \"我不同意他的学习方式，我之前学习到很晚，我就免疫力下降了，生病了，不建议熬夜，熬夜是不可逆的\", \"nickname\": \"吐泡泡的🐟\", \"userid\": \"5d1319e4000000001102bc06\", \"publish_time\": \"2020-12-23\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5dbf1e120666f3000111159c.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"0\"}]', '2021-02-02 13:49:49');
INSERT INTO `lrb_note`(`id`, `note_id`, `note_url`, `title`, `video_poster`, `video_src`, `images_lists`, `content`, `like_num`, `comment_num`, `star_num`, `publish_date`, `author_name`, `author_image`, `author_id`, `description`, `board_id`, `tag_str`, `note_str`, `comment_str`, `spider_time`) VALUES (63, '5f1e42cb000000000101ff80', 'https://www.xiaohongshu.com/discovery/item/5f1e42cb000000000101ff80', '高考英语没救了？进来看看，让你保底100分', '', '', '[\"background-image: url(\\\"https://ci.xiaohongshu.com/968350e7-9c91-5ad2-a78d-0368e17c018b/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/ba187d87-7420-553e-9d3d-dc435fd4f8ba/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/07da83f9-f454-5efc-814a-50b03d001773/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/a003588a-26f2-5963-985e-d2aebd646fce/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/70dec44c-9eda-5bb7-8c7c-e9752bf02856/format/webp\\\");\", \"background-image: url(\\\"https://ci.xiaohongshu.com/850b32ab-5dae-5929-bd8f-e1d5a671a59c/format/webp\\\");\"]', '高考英语是我认为最容易提分的科目，没有之一！注意我说的是“提分”，不是“学好”！\n我用高三一年，将英语成绩从30➕分提高到高考129分，没学语法，不懂音标，诀窍只有一个:背单词！\n①背高考大纲所有的单词\n②背作文模板，背常用单词拼写\n③刷题刷题再刷题\n·\n具体如何做？\n①每次做题的时候，只要不认识的单词，马上记到笔记本上，并经常翻阅\n②将不会的单词抄到纸上贴满全屋\n③每天做一篇完型填空，两篇阅读\n④平时按时完成老师布置的任务，中后期多刷题，培养语感\n·\n问题一:英语音标用不用学？\n我只能说我当时没学\n问题二:英语语法用不用学？\n我只能说我当时没学，如果到高三还一点语法不会，那学的性价比太低，整篇高考英语试卷也没多少分是必须用到语法的，不如去背单词提分效率高！但是过了高考这关，如果想学好英语，必须会语法！\n·\n我最后再说一次，用我的方法只能快速提分，不可能把英语学的多好，而且不管你现在把单词记得多熟，也特别容易忘，所以长远来看，这不是个好方法！但是也不要杠这样做坏处有多少，因为应试教育就是这样，只看你高考成绩是多少，没人管你是不是真会，考上个好大学，有的是机会去好好学英语，考不上好大学，即使现在英语学的多好，有机会去应用吗？对于准高三差生来说，我想任何方法都不如去背单词提分效率高，即使你现在英语0分，把单词背好，高考上100分没问题！\n·\n具体攻略见图片\nPS:今年考研，大纲5600单词，一个月已经背了2800单词（每天都在背新的单词➕不停重复），8月背完剩下的2800单词！\n@校园薯 @薯队长\n', 1519, 54, 912, '2020-07-27 10:58', '一个幸运的伪学霸', 'https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@240w_240h_90q_1e_1c_1x.jpg', '5e3bd70700000000010026b7', '高考英语是我认为最容易提分的科目，没有之一！注意我说的是“提分”，不是“学好”！ 我用高三一年，将英语成绩从30➕分提高到高考129分，没学语法，不懂音标，诀窍只有一个:背单词！ ①背高考大纲所有的单', '5ea80d2f0000000001007dd2', '[]', '[{\"note_id\": \"5f0ef9d0000000000101d0a8\", \"note_desc\": \"N.1｜英语二86分经验干货分享\"}, {\"note_id\": \"5ee048d60000000001000f1b\", \"note_desc\": \"基础差怎么过英语四级\"}, {\"note_id\": \"5ef8a06d0000000001000214\", \"note_desc\": \"七月备战考研到底晚不晚❓告诉你绝对不晚！\"}, {\"note_id\": \"5f1d22c5000000000100bd4b\", \"note_desc\": \"考研英语88，基础中等及以下同学，入手不亏\"}, {\"note_id\": \"5f180e0d0000000001006fc5\", \"note_desc\": \"广东专插本——英语备考经验分享1⃣️\"}, {\"note_id\": \"5efbfd8000000000010031f5\", \"note_desc\": \"高考押题作文\"}, {\"note_id\": \"5ecfbf6a0000000001000df0\", \"note_desc\": \"距离高考39天|易烊千玺可以提分你也可以。\"}, {\"note_id\": \"5f12be01000000000100150d\", \"note_desc\": \"四级610㊙️冲刺9.19四级备考计划🐛\"}, {\"note_id\": \"5ede45300000000001002b0c\", \"note_desc\": \"21考研，6月开始0基础该怎么复习？\"}]', '[{\"comment_text\": \"作为一个仙女英语老师，负责任的说，单词能解决大部分的分数，但是无法帮助你拿到特别高的分数，毕竟不是所有的题目都是考你意思，像高中比如完型，很多都是考语法，所以这就是为什么光靠背单词拿不到高分的原因\", \"nickname\": \"小仙女\", \"userid\": \"5a683277e8ac2b72ffca9e1f\", \"publish_time\": \"2020-08-08\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5a683293d2c8a5132f78500e.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"22\"}, {\"comment_text\": \"哥哥哥哥！化学！！！！\", \"nickname\": \"黄桃\", \"userid\": \"5d202b760000000012017e19\", \"publish_time\": \"2020-07-28\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5f15688d3f0af10001311553.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"11\"}, {\"comment_text\": \"初中英语没学好该怎么提呢，我现在高三了\", \"nickname\": \"觅安宁\", \"userid\": \"5e1d71e90000000001001192\", \"publish_time\": \"2020-07-28\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5f44a3594614f90001f11f16.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"9\"}, {\"comment_text\": \"我都不知道从何下手\", \"nickname\": \"圈圈\", \"userid\": \"5c1a11df000000000703aaab\", \"publish_time\": \"2020-07-27\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5fa272f2a86d5e0001ea3e5d.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"5\"}, {\"comment_text\": \"想起我高考考英语，到阅读时太困了，睡着了\", \"nickname\": \"爱栗子的喵\", \"userid\": \"556c9d553397db19de1b6713\", \"publish_time\": \"2020-10-13\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5fc11638e52d980001de6be3.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"4\"}]', '2021-02-02 13:49:53');
INSERT INTO `lrb_note`(`id`, `note_id`, `note_url`, `title`, `video_poster`, `video_src`, `images_lists`, `content`, `like_num`, `comment_num`, `star_num`, `publish_date`, `author_name`, `author_image`, `author_id`, `description`, `board_id`, `tag_str`, `note_str`, `comment_str`, `spider_time`) VALUES (64, '5e86a765000000000100ab01', 'https://www.xiaohongshu.com/discovery/item/5e86a765000000000100ab01', '高考逆袭|听我的，最后三个月至少提升100分', '', '', '[\"background-image: url(\\\"https://ci.xiaohongshu.com/f7353ff5-8840-59d9-89ef-3f1b675c79b2/format/webp\\\");\"]', '大家好，我是陪伴大家备考的伪学霸！\n高中时期用高三一年完成200分到560分逆袭！\n大学毕业后用两年考下公务员、司法考试（本人非法学专业）、注会5门和税务师！今天我就来分享一下我的高考经验！\n.\n高考延期，对于谁最有利？差等生！因为提升空间大！对于那些动不动就考600多分的学霸，再延期一年，最后高考也就那样！差等生们，逆袭机会来啦！\n.\n最后三个月还想提升100分，怎么做？\n一、刷到我，说明到这时候，你还不想为自己拼一次！都什么时候了，你还有时间刷小红书？我高三一年，一次手机都没用过！手机最让人分心，想逆袭，第一步，把手机上交家长，顺便说一句，爸爸妈妈，你们看好了，从今天开始到高考，我最少提升100分，相信你的父母也会觉得你长大了！\n.\n二、睡眠时间就牺牲一点吧！不要说什么对身体不好，坚持3个月少睡眠对身体再不好能怎么样！我高三一年天天坚持学到深夜2点，早晨6点起床背单词！你以为逆袭的那么容易？得吃苦啊，兄弟！但是注意，考前两个星期，要调整到正常作息时间！\n.\n三、要自信，要有梦想！努力真的永远都不晚！考上好大学重要吗？重要，但没那么重要！清华北大出来的，混不下去的有，高中都没毕业的人靠做生意成土豪的也大有人在！什么样的人才能成功？具有顽强拼搏精神、永不服输的人！这种人，即使大学没上，一样会成功！因为他们不甘于平庸！\n.\n下面给大家说一下各科怎么学！\n语文:首先作文是大分，肯定得拿！必须每天背一些时事素材，到考场上作文才有的写！今年的疫情一定是个绕不过去的主题，多积累素材！其次就是多刷题，刷题不是只有理科才可以！多做题，才能有题感！\n.\n英文:如果这时候语法还不会，放弃！现在学性价比太低。首先要背单词，拿出高中必背单词词典，最好全背下来！你会发现做英语完形填空和阅读时，和做语文理解一样，你肯定能做对！第二就是背作文模板，考试时往里套，就算得不了高分，平均分一定有了！我用这种方法，不会一点英语语法，高考英语129！\n.\n数学、理综、文综:多刷题！现在才开始由知识点→做题的方法学习应该效果不太好！所以可以由做题引出重点知识点！这三科知识点虽然多且杂，但是高考经常考的也就那几个！这时候要学会放弃，要学会抓重点！\n.\n我用高三一年时间由200多分逆袭到高考的560分，而现在让你用最后的3个月增加100分，应该也可以做到吧？除非你自己就认输了，那你还真让人看不起啊！@校园薯\n', 2236, 259, 1157, '2020-04-03 11:03', '一个幸运的伪学霸', 'https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@240w_240h_90q_1e_1c_1x.jpg', '5e3bd70700000000010026b7', '大家好，我是陪伴大家备考的伪学霸！ 高中时期用高三一年完成200分到560分逆袭！ 大学毕业后用两年考下公务员、司法考试（本人非法学专业）、注会5门和税务师！今天我就来分享一下我的高考经验！ . 高考', '5ea80d2f0000000001007dd2', '[]', '[{\"note_id\": \"5e82c2880000000001008d84\", \"note_desc\": \"❗️高考延期一个月｜北大学姐教你最强逆袭！\"}, {\"note_id\": \"5e782774000000000100bf99\", \"note_desc\": \"两年通关公考、法考、注会5门、税务师|经验\"}, {\"note_id\": \"5e75fb5a0000000001002449\", \"note_desc\": \"数学从五十分到一百二十分\"}, {\"note_id\": \"5e6f8576000000000100a307\", \"note_desc\": \"🔥同学们要的教辅推荐来啦！总有适合你的\"}, {\"note_id\": \"5e4913740000000001009e43\", \"note_desc\": \"高中数学149分的经验分享～\"}, {\"note_id\": \"5e60f1570000000001003675\", \"note_desc\": \"北大学姐历史90分答题术语\"}, {\"note_id\": \"5e5c7602000000000100522b\", \"note_desc\": \"备战高考｜北大学长的语文作文55+秘籍（一）\"}, {\"note_id\": \"5e5851af0000000001008419\", \"note_desc\": \"北大学姐文数149带你逆袭高考数学（一）\"}, {\"note_id\": \"5e64e5890000000001002fc8\", \"note_desc\": \"曾三个月提150分🌟中大学姐带你逆袭上985\"}, {\"note_id\": \"5e68bcac000000000100bac9\", \"note_desc\": \"拒绝🙅🏻‍♀️玩手机🤳好好读书📖\"}]', '[{\"comment_text\": \"如果你是差等生，你很幸运！因为你有一鸣惊人的机会！而一鸣惊人的感觉，我体会过，太爽！比一直学习好最后考上北大的感觉都好！因为那些人是正常发展的结果，而咱们，叫逆袭！是特殊的！\", \"nickname\": \"一个幸运的伪学霸(作者)\", \"userid\": \"5e3bd70700000000010026b7\", \"publish_time\": \"2020-04-03\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5e88b37c72946e0001b3b191.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"437\"}, {\"comment_text\": \"为什么刚开始只有二百分？\", \"nickname\": \"那就这样吧\", \"userid\": \"59be78615e87e735790d852d\", \"publish_time\": \"2020-04-05\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5f5833ad61d5d70001f9848b.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"54\"}, {\"comment_text\": \"我现在450分，明年高考能到600吗？\", \"nickname\": \"橙子女 AZURA\", \"userid\": \"59c6958b44363b4b509f3094\", \"publish_time\": \"2020-04-13\", \"avatar\": \"https://img.xiaohongshu.com/avatar/60103ae9ba8318346864b031.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"26\"}, {\"comment_text\": \"高三了，但我只是200多分，我想拼但我怕自己坚持不了怎么办\", \"nickname\": \"♡Fairy~~\", \"userid\": \"5f38919c0000000001004956\", \"publish_time\": \"2020-08-17\", \"avatar\": \"https://img.xiaohongshu.com/avatar/5f38919c0000000001004956.jpg@80w_80h_90q_1e_1c_1x.jpg\", \"like_num\": \"11\"}, {\"comment_text\": \"我还有一年 我可以的\", \"nickname\": \"魅力人机\", \"userid\": \"5bb32ee