

CREATE TABLE `lrb_note` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增id',
  `note_id` varchar(30) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '笔记id',
  `note_url` varchar(150) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '笔记链接',
  `title` varchar(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '笔记标题',
  `video_poster` varchar(120) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '视频封面',
  `video_src` varchar(250) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '视频链接',
  `images_lists` text COLLATE utf8mb4_general_ci NOT NULL COMMENT '图片列表/json',
  `content` text COLLATE utf8mb4_general_ci NOT NULL COMMENT '笔记正文',
  `like_num` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '点赞数',
  `comment_num` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '评论数',
  `star_num` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '收藏数',
  `publish_date` varchar(20) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '发布时间',
  `author_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '作者名字',
  `author_image` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '作者头像',
  `author_id` varchar(30) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '作者用户id',
  `description` varchar(300) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '笔记简介',
  `board_id` varchar(30) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '作者用户id',
  `tag_str` text COLLATE utf8mb4_general_ci NOT NULL COMMENT '关联标签列表/json',
  `note_str` text COLLATE utf8mb4_general_ci NOT NULL COMMENT '相关笔记列表/json',
  `comment_str` text COLLATE utf8mb4_general_ci NOT NULL COMMENT '评论列表/json',
  `spider_time` char(19) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '爬取时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `note_id` (`note_id`) USING BTREE,
  KEY `author_id` (`author_id`) USING BTREE,
  KEY `board_id` (`board_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci ROW_FORMAT=DYNAMIC COMMENT='小红书笔记表';


